var zombie;
zombie = require("./browser");
exports.Browser = zombie.Browser;
exports.package = zombie.package;
exports.version = zombie.version;
exports.visit = function(url, options, callback) {
  return new zombie.Browser(options).visit(url, options, callback);
};
exports.listen = function(port, callback) {
  return require("./protocol").listen(port, callback);
};