var WebSocket, core;
core = require("jsdom").dom.level3.core;
WebSocket = require('websocket-client').WebSocket;
exports.use = function() {
  var extend;
  extend = function(window) {
    return window.WebSocket = function(url, proto, opts) {
      return new WebSocket(url, proto, opts);
    };
  };
  return {
    extend: extend
  };
};